INSERT INTO personal_bot.t_health_conditions (name, description)
VALUES
    ('Diabetes', 'A metabolic disease characterized by high blood sugar levels.'),
    ('Obesity', 'A condition characterized by excess body fat that increases the risk of health problems.'),
    ('Heart Disease', 'A group of conditions that affect the structure and function of the heart.'),
    ('Hypertension', 'High blood pressure, a condition in which the force of blood against the artery walls is too high.'),
    ('Celiac Disease', 'An autoimmune disorder in which the ingestion of gluten leads to damage in the small intestine.'),
    ('Crohn''s Disease', 'A type of inflammatory bowel disease that can affect any part of the gastrointestinal tract.'),
    ('Ulcerative Colitis', 'A chronic inflammatory bowel disease that causes inflammation and ulcers in the digestive tract.'),
    ('Gallstones', 'Hard deposits that form in the gallbladder and can cause severe abdominal pain.'),
    ('Kidney Disease', 'A condition in which the kidneys are damaged and cannot filter blood properly.'),
    ('Liver Disease', 'A condition in which the liver is damaged and cannot function properly.'),
    ('Pancreatitis', 'An inflammation of the pancreas that can cause severe abdominal pain.'),
    ('Polycystic Ovary Syndrome (PCOS)', 'A hormonal disorder that can cause irregular periods and difficulty getting pregnant.'),
    ('Thyroid Disorders', 'Conditions that affect the thyroid gland and can cause a variety of symptoms, including weight gain or loss.'),
    ('Inflammatory Arthritis', 'A type of arthritis that causes inflammation in the joints.'),
    ('Osteoporosis', 'A condition in which bones become weak and brittle.');
